import React, {Component} from 'react';

class Producto extends Component{
    render(){
        const {nombre, precio} = this.props.producto
        return(
            <div>
                <h3>{this.props.producto.nombre}</h3>
                <h2>{nombre}</h2>
                <p>precio: ${precio}</p>
            </div>
        )
    }
}

export default Producto;
